import React from 'react';
import { Grid2, Button, Typography, Box } from '@mui/material';
import PokemonCard from './pokemonCard';
import { Pokemon } from '../schema/pokemon/pokemon.entity';
import { BattleResult } from '../schema/battle-result/battle-result.entity';

interface BattleArenaProps {
  pokemon1: Pokemon;
  pokemon2: Pokemon;
  onBattleStart: () => void;
  battleResult: BattleResult | null;
}

const BattleArena: React.FC<BattleArenaProps> = ({ pokemon1, pokemon2, onBattleStart, battleResult }) => {
  return (
    <Box mt={4}>
      <Grid2 container spacing={2} justifyContent={'center'}>
        <Grid2 size={4}>
          <PokemonCard pokemon={pokemon1} />
        </Grid2>
        <Grid2 size={2}>
          <Box display="flex" flexDirection="column" alignItems="center" justifyContent="center" height="100%">
            <Button variant="contained" color="primary" onClick={onBattleStart}>
              Start Battle
            </Button>
            {battleResult && (
              <Typography variant="h6" mt={2}>
                {battleResult.winnerId === pokemon1.id ? pokemon1.name : pokemon2.name} wins!
              </Typography>
            )}
          </Box>
        </Grid2>
        <Grid2 size={4}>
          <PokemonCard pokemon={pokemon2} />
        </Grid2>
      </Grid2>
    </Box>
  );
}

export default BattleArena;
