import React from 'react';
import { Card, CardContent, CardMedia, Typography, LinearProgress, Box } from '@mui/material';
import { Pokemon } from '../schema/pokemon/pokemon.entity';

interface PokemonCardProps {
  pokemon: Pokemon;
}

const PokemonCard: React.FC<PokemonCardProps> = ({ pokemon }) => {
  const stats: (keyof Pokemon)[] = ['hp', 'attack', 'defense', 'speed'];

  return (
    <Card>
      <CardMedia
        component="img"
        height="200"
        image={pokemon.imageUrl}
        alt={pokemon.name}
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {pokemon.name}
        </Typography>
        {stats.map(stat => (
          <Box key={stat}>
            <Typography variant="body2" color="text.secondary">
              {stat.toUpperCase()}
            </Typography>
            <LinearProgress variant="determinate" value={Number(pokemon[stat]) / 100 * 100} />
          </Box>
        ))}
      </CardContent>
    </Card>
  );
}

export default PokemonCard;
